import React, { useState } from 'react';
import './App.css';

interface Note {
  id: number;
  title: string;
  content: string;
  label: string;
  isFavorite: boolean; // New property for tracking favorite status
}

const App: React.FC = () => {
  const [notes, setNotes] = useState<Note[]>([]);
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [label, setLabel] = useState('');

  const handleCreateNote = () => {
    if (!title || !content || !label) return; // Basic validation
    const newNote: Note = {
      id: Date.now(),
      title,
      content,
      label,
      isFavorite: false, // Default favorite status
    };
    setNotes([...notes, newNote]);
    setTitle('');
    setContent('');
    setLabel('');
  };

  const toggleFavorite = (id: number) => {
    setNotes(notes.map(note =>
      note.id === id ? { ...note, isFavorite: !note.isFavorite } : note
    ));
  };

  const handleDeleteNote = (id: number) => {
    setNotes(notes.filter(note => note.id !== id));
  };

  return (
    <div className="app-container">
      <div className="note-form">
        <input
          type="text"
          placeholder="Note Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Note Content"
          value={content}
          onChange={(e) => setContent(e.target.value)}
        />
        <select value={label} onChange={(e) => setLabel(e.target.value)}>
          <option value="">--Select your label--</option>
          <option value="personal">Personal</option>
          <option value="work">Work</option>
          <option value="study">Study</option>
          <option value="other">Other</option>
        </select>
        <button onClick={handleCreateNote}>Create Note</button>
      </div>

      <div className="notes-grid">
        {notes.map(note => (
          <div key={note.id} className="note-item">
            <div className="note-header">
              <h3>{note.title}</h3>
              <button 
                className={`favorite-btn ${note.isFavorite ? 'favorited' : ''}`} 
                onClick={() => toggleFavorite(note.id)}
              >
                &#x2764; {/* Heart symbol */}
              </button>
              <button className="delete-btn" onClick={() => handleDeleteNote(note.id)}>x</button>
            </div>
            <p>{note.content}</p>
            <small>{note.label}</small>
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
